import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useUserContext } from '../context/UserContext';
import { 
  TextField, 
  Button, 
  Container, 
  Grid, 
  MenuItem, 
  Typography,
  Box
} from '@mui/material';

const UserForm = ({ editMode }) => {
  const { id } = useParams();
  const { users, addUser, updateUser } = useUserContext();
  const navigate = useNavigate();
  
  const userToEdit = users.find(user => user.id === parseInt(id));
  
  const [user, setUser] = useState({
    firstName: editMode ? userToEdit?.firstName || '' : '',
    lastName: editMode ? userToEdit?.lastName || '' : '',
    height: editMode ? userToEdit?.height || '' : '',
    weight: editMode ? userToEdit?.weight || '' : '',
    gender: editMode ? userToEdit?.gender || 'male' : 'male',
    address: editMode ? userToEdit?.address || '' : '',
    photo: editMode ? userToEdit?.photo || '' : '',
  });

  const [errors, setErrors] = useState({});

  const validate = () => {
    const newErrors = {};
    if (!user.firstName) newErrors.firstName = 'First name is required';
    if (!user.lastName) newErrors.lastName = 'Last name is required';
    if (user.height <= 0) newErrors.height = 'Height must be positive';
    if (user.weight <= 0) newErrors.weight = 'Weight must be positive';
    if (!user.address) newErrors.address = 'Address is required';
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = e => {
    const { name, value } = e.target;
    setUser(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    
    if (!validate()) return;
    
    try {
      if (editMode) {
        await updateUser(id, user);
      } else {
        await addUser(user);
      }
      navigate('/');
    } catch (error) {
      console.error('Error saving user:', error);
    }
  };

  return (
    <Container maxWidth="sm">
      <Box mt={4}>
        <Typography variant="h5" gutterBottom>
          {editMode ? 'Edit User' : 'Create New User'}
        </Typography>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="First Name"
                name="firstName"
                value={user.firstName}
                onChange={handleChange}
                error={!!errors.firstName}
                helperText={errors.firstName}
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Last Name"
                name="lastName"
                value={user.lastName}
                onChange={handleChange}
                error={!!errors.lastName}
                helperText={errors.lastName}
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Height (cm)"
                name="height"
                type="number"
                value={user.height}
                onChange={handleChange}
                error={!!errors.height}
                helperText={errors.height}
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Weight (kg)"
                name="weight"
                type="number"
                value={user.weight}
                onChange={handleChange}
                error={!!errors.weight}
                helperText={errors.weight}
                required
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                select
                label="Gender"
                name="gender"
                value={user.gender}
                onChange={handleChange}
              >
                <MenuItem value="male">Male</MenuItem>
                <MenuItem value="female">Female</MenuItem>
                <MenuItem value="other">Other</MenuItem>
              </TextField>
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Address"
                name="address"
                value={user.address}
                onChange={handleChange}
                error={!!errors.address}
                helperText={errors.address}
                required
                multiline
                rows={3}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Photo URL"
                name="photo"
                value={user.photo}
                onChange={handleChange}
                placeholder="https://example.com/photo.jpg"
              />
            </Grid>
            <Grid item xs={12}>
              {user.photo && (
                <Box mt={2} textAlign="center">
                  <img 
                    src={user.photo} 
                    alt="Preview" 
                    style={{ maxWidth: '100%', maxHeight: 200, borderRadius: 4 }}
                  />
                </Box>
              )}
            </Grid>
            <Grid item xs={12}>
              <Button 
                type="submit" 
                variant="contained" 
                color="primary"
                fullWidth
              >
                {editMode ? 'Update User' : 'Create User'}
              </Button>
            </Grid>
          </Grid>
        </form>
      </Box>
    </Container>
  );
};

export default UserForm;
